package com.salesforce.pages;

import com.framework.selenium.api.base.SeleniumBase;
import com.framework.selenium.api.design.Locators;

public class IncidentActivityPage extends SeleniumBase{

	public IncidentActivityPage typeAndSharePost(String post) {
		clickUsingJs(locateElement(Locators.XPATH, "//button[@title='Share an update...']"));
		clearAndType(locateElement(Locators.XPATH, "//div[@data-placeholder='Share an update...']"), post);
		clickUsingJs(locateElement(Locators.XPATH,"//button[text()='Share']"));
		reportStep("Post "+post+" is shared", "pass");
		return this;
	}
	
	public IncidentActivityPage clickPollTab() {
		clickUsingJs(locateElement(Locators.XPATH,"//a[@title='Poll']"));
		reportStep("poll tab is clicked", "pass");
		return this;
	}
	
	public IncidentActivityPage clickQuestionTab() {
		clickUsingJs(locateElement(Locators.XPATH,"//a[@data-tab-name='FeedItem.QuestionPost']"));
		reportStep("question tab is clicked", "pass");
		return this;
	}
	
	public IncidentActivityPage typePollQuestion(String pollQuestion) {
		clearAndType(locateElement(Locators.XPATH,"//textarea[@placeholder='What would you like to ask?']"), pollQuestion);
		reportStep("poll question "+pollQuestion+" is entered", "pass");
		return this;
	}
	
	public IncidentActivityPage typeChoiceOne(String firstChoice) {
		clearAndType(locateElement(Locators.XPATH,"//span[text()='Choice 1']/following::input[1]"), firstChoice);
		reportStep("first choice "+firstChoice+" is entered", "pass");
		return this;
	}
	
	
	
	public IncidentActivityPage clickAddNewChoice() {
		clickUsingJs(locateElement(Locators.XPATH,"//button[text()='Add new choice']"));
		reportStep("New choice filed is added", "pass");
		return this;
	}
	
	
	public IncidentActivityPage sortByLatestPost() {
		click(Locators.XPATH,"//label[text()='Sort by:']/following::button[1]");
		click(Locators.XPATH,"//lightning-base-combobox-item[@data-value='CreatedDateDesc']");
		reportStep("Sorted the post by latest", "pass");
		return this;
	}
}

package com.salesforce.pages;

import com.framework.selenium.api.base.SeleniumBase;
import com.framework.selenium.api.design.Locators;

public class IncidentPage extends SeleniumBase{

	public IncidentPage verifyIncidentPage() {
		verifyDisplayed(locateElement(Locators.XPATH, "//h1[text()='Incidents']"));
		reportStep("Incident page is loded", "pass");
		return this;
	}
	
	public NewIncidentPage clickNewButton() {
		click(Locators.XPATH,"//a[@title='New']");
		reportStep("New button is clicked", "pass");
		return new NewIncidentPage();
	}
	
	public IncidentPage typeAndSearchIncident(String incidentNumber) {
		typeAndEnter(locateElement(Locators.XPATH, "//input[@placeholder='Search this list...']"), incidentNumber);
		reportStep("Incident number "+incidentNumber+" is searched", "pass");
		return this;
	}
	
	public IncidentPage verifyIncidentNumber(String expectedIncidentNumber) {
		verifyExactText(locateElement(Locators.XPATH,"//table[@aria-label='Recently Viewed']/tbody/tr[1]/th[1]"), expectedIncidentNumber);
		reportStep("Incident number "+expectedIncidentNumber+" is verified", "pass");
		return this;
	}

    public IncidentPage clickFirstIncidentNumber() {
		click(Locators.XPATH,"//table[@aria-label='Recently Viewed']/tbody/tr[1]/th[1]//a");
		reportStep("First incident button is clicked", "pass");
		return this;
	}

	public IncidentPage clickEditButton() {
		click(Locators.XPATH,"//table[@aria-label='Recently Viewed']/tbody/tr[1]/td[8]");
		click(Locators.XPATH,"//a[@title='Edit']");
		reportStep("Edit button is clicked", "pass");
		return this;
	}
    
	
	public IncidentPage deleteIncident() {
		click(Locators.XPATH,"//table[@aria-label='Recently Viewed']/tbody/tr[1]/td[8]");
		click(Locators.XPATH,"//a[@title='Delete']");
		click(Locators.XPATH,"//button[@title='Delete']");
		reportStep("Incident is deleted successfully", "pass");
		return this;
	}
	
	public IncidentPage verifyToasterMessage() {
		verifyDisplayed(locateElement(Locators.XPATH, "//div[@data-aura-class='forceToastMessage']"));
		reportStep("Toaster message is verified", "pass");
		return this;
	}
	
	
}

